mapper.select:
SELECT f.alais,f.position,f.sensortype,f.unit,f.limit_alarms,f.limit_powers,f.floor_alarms,f.floor_powers,f.limit_repowers,f.floor_repowers,DATE_FORMAT(e.statistictime,'%Y-%m-%d %H:%i:00') statistictime,
e.maxvalues maxvalues,DATE_FORMAT(e.maxtime,'%Y-%m-%d %H:%i:%S') maxtime,e.minvalue,DATE_FORMAT(e.mintime,'%Y-%m-%d %H:%i:%S') mintime,CONVERT(e.avgvalue,DECIMAL(10,2)) avgvalue,e.status,e.debug FROM 
(SELECT ip,sensorId,sensor_type,statistictime,maxvalues,maxtime,minvalue,mintime,avgvalue,status,debug 
FROM t_analog_statistics 
WHERE statistictime>=#{starttime} AND statistictime<=#{endtime}
AND remark=#{itype})e
INNER JOIN
(SELECT a.id,c.ipaddr,a.sensorId,a.sensor_type,d.k unit,d.v sensortype,a.limit_alarm limit_alarms,a.floor_alarm floor_alarms,
a.limit_power limit_powers,a.floor_power floor_powers,a.limit_repower limit_repowers,a.floor_repower floor_repowers,a.sensor_position,b.v position,a.alais FROM t_sensor a
LEFT JOIN t_static b ON a.sensor_position=b.id
LEFT JOIN t_static d ON a.sensor_type=d.id
LEFT JOIN t_substation c ON a.station=c.id )f ON e.ip=f.ipaddr AND e.sensorId=f.sensorId AND e.sensor_type=f.sensor_type


		f.id=#{id}


		AND e.ip=#{ip}


		AND f.sensor_position=#{sensor_position}


limit #{start_row},#{page_rows}

SELECT COUNT(e.ip) FROM (SELECT ip,sensorId,sensor_type,sensortype,unit,statistictime,maxvalues,maxtime,minvalue,mintime,avgvalue,status,debug FROM t_analog_statistics WHERE statistictime>=#{starttime} AND statistictime<=#{endtime} AND remark=#{itype})e INNER JOIN (SELECT a.id,c.ipaddr,a.sensorId,a.sensor_type,a.limit_alarm,a.limit_power,a.limit_repower,b.v position,a.alais FROM t_sensor a LEFT JOIN t_static b ON a.sensor_position=b.id LEFT JOIN t_substation c ON a.station=c.id)f ON e.ip=f.ipaddr AND e.sensorId=f.sensorId AND e.sensor_type=f.sensor_type
f.id=#{id}
AND e.ip=#{ip}
