mapper.select:
SELECT tr.id,tr.station,sub.station_name station_name,sub.ipaddr ipaddr, tr.sensorId,tct.id sensor_type,tct.pid,tct.k sensorUnit,tct.v type,tct.path, tcp.id sensor_position,tcp.v position,tct.path2, tr.control,tr.input_type,tr.min_range,tr.middle_rang,tr.max_range,tr.min_frequency,tr.max_frequency, tr.n_point,tr.e_point,tr.alais,tr.period,tr.fluctuation_range, tr.error_band,tr.limit_warning,tr.floor_warning,tr.limit_alarm,tr.floor_alarm,tr.area_id,tas.areaname, tr.limit_power,tr.floor_power,tr.limit_repower,tr.floor_repower,tr.isDrainage,tds.drainageId menu,tr.iscalc, tr.uid,tr.drainageId,tr.x_point,tr.y_point, tr.calibration,tr.upgrade3,tr.upgrade2,tr.upgrade1,tr.position_type_id,tpt.`name` position_type,tr.upper_level1,tr.upper_level2,tr.upper_level3, tr.upper_level4,tr.floor_level1,tr.floor_level2,tr.floor_level3,tr.floor_level4,tr.direction,tr.x2_point,tr.y2_point,tr.x3_point,tr.y3_point, tr.n2_point,tr.e2_point,tr.n3_point,tr.e3_point FROM t_sensor tr LEFT JOIN t_static tcp ON tr.sensor_position=tcp.id LEFT JOIN t_substation sub ON tr.station=sub.id LEFT JOIN t_static tct ON tr.sensor_type=tct.id LEFT JOIN t_area tas ON tr.area_id=tas.id LEFT JOIN t_drainage_sensor tds ON tr.id=tds.sensorId LEFT JOIN t_position_type tpt on tr.position_type_id=tpt.id where tr.control!=3
and tr.sensor_type=#{type}
ORDER BY sub.ipaddr,tr.sensorId
