mapper.select:
SELECT f.id sensor_id,f.ipaddr ip,f.sensorId,f.sensor_type sensor_type,DATE_FORMAT(k.starttime,'%Y-%m-%d %H:%i:%S') starttime,DATE_FORMAT(k.endtime,'%Y-%m-%d %H:%i:%S') endtime, f.k unit,f.v sensortype,f.position,f.limit_alarm,f.floor_alarm,f.limit_power,f.floor_power,f.limit_repower,f.floor_repower,f.alais alais FROM (SELECT e.id id,d2.ipaddr,e.sensorId,d.k,d.v v,d1.v position,e.sensor_type,e.limit_alarm,e.floor_alarm, e.limit_power,e.floor_power,e.limit_repower,e.floor_repower,e.alais FROM t_sensor e LEFT JOIN t_static d ON e.sensor_type=d.id LEFT JOIN t_static d1 ON e.sensor_position=d1.id LEFT JOIN t_substation d2 ON e.station=d2.id
e.control<3
AND e.id in
#{id}
AND e.area_id=#{areaId}
AND e.sensor_position=#{positionId}
)f LEFT JOIN (SELECT a.ip,a.sensor_id,a.type sensor_type,DATE_FORMAT(a.starttime,'%Y-%m-%d %H:%i:%S') starttime, DATE_FORMAT(b.endTime,'%Y-%m-%d %H:%i:%S') endtime FROM (SELECT ip,sensor_id,type,MAX(startTime) starttime FROM t_analoginfo_query WHERE status=2 AND endId>0 GROUP BY ip,sensor_id,type)a INNER JOIN (SELECT ip,sensor_id,type,startTime,endTime FROM t_analoginfo_query WHERE status=2 AND endId>0)b ON a.ip=b.ip AND a.sensor_id=b.sensor_id AND a.type=b.type AND a.starttime=b.startTime )k ON f.ipaddr=k.ip AND f.sensorId=k.sensor_id AND f.sensor_type=k.sensor_type

SELECT e.id sensor_id,e.ipaddr ip,e.sensorId,e.alais,e.sensor_type,e.type sensortype,e.position,DATE_FORMAT(h.starttime,'%Y-%m-%d %H:%i:%S') starttime,
DATE_FORMAT(h.endtime,'%Y-%m-%d %H:%i:%S') endtime,e.alarm_status,e.alarmstatus FROM
(SELECT a.id,d.ipaddr,a.sensorId,a.sensor_type,a.alais,b.v type,c.v position,a.alarm_status,b.k alarmstatus FROM t_switch_sensor a
LEFT JOIN t_static b ON a.sensor_type=b.id
LEFT JOIN t_static c ON a.sensor_position=c.id
LEFT JOIN t_substation d ON a.station=d.id

	a.control<3

	AND a.id in

		#{id}



	AND a.area_id=#{areaId}


	AND a.sensor_position=#{positionId}

)e
LEFT JOIN 
(SELECT a.ip,a.sensor_id,a.type,b.startTime starttime,b.endTime endtime FROM 
(SELECT ip,sensor_id,type,MAX(startTime) startTime FROM t_analoginfo_query WHERE status=2 GROUP BY ip,sensor_id,type)a
INNER JOIN (SELECT ip,sensor_id,type,startTime,endTime FROM t_analoginfo_query WHERE status=2
) b ON a.ip=b.ip AND a.sensor_id=b.sensor_id AND a.type=b.type AND a.startTime=b.startTime
) h ON e.ipaddr = h.ip AND e.sensor_type=h.type AND e.sensorId=h.sensor_id

SELECT a.ip,b.sensorId,b.sensor_type,a.status,a.value minvalue,a.debug,b.alais,b.position,b.sensortype,DATE_FORMAT(a.responsetime,'%Y-%m-%d %H:%i:%S') starttime,a.feedback,b.alarm_status,b.alarmstatus FROM (SELECT ip,devid,type,value,status,responsetime,debug,feedback FROM t_coalMine WHERE responsetime>DATE_SUB(NOW(),INTERVAL 5 MINUTE) AND status<5)a INNER JOIN (SELECT b.ipaddr,a.sensorId,a.sensor_type,d.v sensortype,c.v position,a.alais,a.uid,a.alarm_status,d.k alarmstatus FROM t_switch_sensor a LEFT JOIN t_substation b ON a.station=b.id LEFT JOIN t_static c ON a.sensor_position=c.id LEFT JOIN t_static d ON a.sensor_type=d.id)b ON a.ip=b.ipaddr AND a.devid=b.sensorId AND a.type=b.sensor_type
b.uid=#{uid}
ORDER BY a.devid,a.type,a.responsetime
