mapper.select:
SELECT tss.id,tss.station station,sub.station_name station_name,sub.ipaddr ipaddr, tss.sensorId,tss.control,tss.input_type,tst.id sensor_type,tst.pid,tst.k type0x,tst.path,tst.path2, tss.n_point,tss.e_point,tst.v type,tss.alarm_status,tss.sensor_position,tsp.v position, tss.power_status,tss.break_display,tsb.v break_d,tss.connect_display,tsc.v connect_d,tss.alais, tss.feedId,tss.isDrainage,tss.uid,tss.drainageId,tss.x_point,tss.y_point, tss.position_type_id,tpt.`name` position_type,tst.islogical,tss.power_scope,tss.device_name,tss.x2_point,tss.y2_point,tss.x3_point,tss.y3_point, tss.n2_point,tss.e2_point,tss.n3_point,tss.e3_point,tss.direction FROM t_switch_sensor tss LEFT JOIN t_substation sub ON tss.station=sub.id LEFT JOIN t_static tsp ON tsp.id=tss.sensor_position LEFT JOIN t_static tst ON tss.sensor_type=tst.id LEFT JOIN t_static tsb ON tss.break_display=tsb.id LEFT JOIN t_static tsc ON tss.connect_display=tsc.id LEFT JOIN t_position_type tpt ON tss.position_type_id=tpt.id where tss.control!=3
and sensor_type=#{type}
ORDER BY sub.ipaddr,tss.sensorId
